import React, { useEffect, useState } from "react";
import BookDetails from "./BookDetails";
// import Book from "./BookList";

function Context() {

  const [books, setBooks] = useState([]);
  useEffect(() => {
    fetch("http://localhost:3001/books")
      .then((response) => response.json())
      .then((data) => {
        return (
          setBooks(data)
        )
      })
      .catch((error) => console.log(error));
  }, [])

  return (
    <div>

        <tr>
          <th>Title</th>
          <th>Author</th>
          <th>Publication</th>
          <th>Edition</th>
          <th>Price</th>
        </tr>

      {books.map((book, i) => (

          // <table>

            <BookDetails 
            key={book.id || i}
            title={book.title}
            author={book.author}
            publication={book.publication}
            edition={book.edition}
            price={book.price} />


      ))
      }

    </div>
  )
//table iko sawa? yes imebaki styling bt on the far end add i wanted to add remove n delete

}
export default Context;
