import React, {useRef,useState} from 'react';
import {FaSearch} from "react-icons/fa";
import "./styles/SearchForm.css";

const SearchForm = ({ setBooks }) => {

  const searchText = useRef('');
  const [searches, setSearches] = useState('');

  const searchChange = (e)=>{
    setSearches(e.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:3001/books")
      .then((response) => response.json())
      .then((data) =>{
        if(data){
          setBooks(data.filter(item=> item.title.toLowerCase().includes(searches)))

        }
      })
      .catch((error) => console.log(error));
  };

  return (
    <div className='header-content flex flex-c text-center text-purple'>
                <h2 className='header-title text-capitalize'>find your book of choice.</h2><br />
                <p className='header-text fs-18 fw-3'>Libraries will get you through times of no money better than money will get you through times of no libraries.<br/>     
                     </p>
    <div className='search-form'>
      <div className='container'>
        <div className='search-form-content'>
          <form className='search-form' onSubmit={handleSubmit}>
            <div className='search-form-elem flex flex-sb bg-white'>
              <input type = "text" className='form-control' value={searches} onChange={searchChange} placeholder='The Lost Word ...' ref = {searchText} />
              <button type = "submit" className='flex flex-c' onClick={handleSubmit}>
                <FaSearch className='text-purple' size = {30} />
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
    </div>
  )
}

export default SearchForm;
